      <figure class="card">
        <div class="card__aspect-ratio">
          <img class="card__image"/>
        </div>
        <button class="card__delete-button button" type="button" aria-label="Удалить"></button>
        <figcaption class="card__container-caption">
          <h2 class="card__caption"></h2>
          <button class="card__like-button" type="button" aria-label="Лайк"></button>
          <p class="card__like-counter"></p>
        </figcaption>
      </figure>
    </li>
  </template>
  <template id="preview-user-template">
    <img class="tooltip-likes__avatar"/>
  </template> */}
    </div>
  );
}

export default App;
