import React from "react";

function Tooltip(props) {
  const [isOpen, setOpen] = React.useState(props.isOpen);
  const [position, setPosition] = React.useState({ top: 0, left: 0 });

  React.useEffect(() => {
    // function changePosition(event) {
      setPosition({
        top: props.position.top,
        left: props.position.left
      })
    // }

    // document.addEventListener('mousemove', changePosition);

    return () => {
      // document.removeEventListener('mousemove', changePosition);
    };
  }, isOpen);

  return (
    <div className={`tooltip-likes ${isOpen && 'popup_opened'}`}
      style={{
            top: position.top,
            left: position.left
          }}>
      {props.likes.map((like, i) => (
        <div key={like._id}>
          <img src={like.avatar} className="tooltip-likes__avatar" />
        </div>
      ))}
    </div>
  );
}

export default Tooltip;
