import React from "react";
import api from "../utils/Api";

function Main(props) {
  const [userName, setUserName] = React.useState();
  const [userDescription, setUserDescription] = React.useState();
  const [userAvatar, setUserAvatar] = React.useState();
  const [cards, setCards] = React.useState([]);

  React.useEffect(() => {
    api.getUserInfo()
      .then((dataFromServer) => {
        setUserName(dataFromServer.name);
        setUserDescription(dataFromServer.about);
        setUserAvatar(dataFromServer.avatar);
      })
      .catch((error) => {
        console.log(error);
      });
  }, []);

  React.useEffect(() => {
    api.getInitialCards()
      .then((cardsFromServer) => {
        setCards(cardsFromServer);
      })
      .catch((error) => {
        console.log(error);
      });
  }, []);

  return(
    <main className="content">
      <section className="profile">
        <img src={userAvatar} alt="Аватар" className="profile__avatar"/>
        <div
          onClick={props.onEditAvatar}
          className="profile__avatar-overlay"
        >
        </div>
        <div className="profile__info">
          <h1 className="profile__name">{userName}</h1>
          <button
            onClick={props.onEditProfile}
            className="profile__edit-button button"
            type="button"
            aria-label="Редактировать"
          >
          </button>
          <p className="profile__about">{userDescription}</p>
        </div>
        <button
          onClick={props.onAddPlace}
          className="profile__add-button button"
          type="button"
          aria-label="Добавить картинку"
        >
        </button>
      </section>
      <section className="gallery">
        <ul className="gallery__list">
          {cards.map((card, i) => (
            <li key={card._id} className="gallery__list-item">
              <figure className="card">
                <div className="card__aspect-ratio">
                  <img src={card.link} className="card__image"/>
                </div>
                <button className="card__delete-button button" type="button" aria-label="Удалить"></button>
                <figcaption className="card__container-caption">
                  <h2 className="card__caption">{card.name}</h2>
                  <button className="card__like-button" type="button" aria-label="Лайк"></button>
                  <p className="card__like-counter">{card.likes.lenght}</p>
                </figcaption>
              </figure>
            </li>
          ))}
        </ul>
      </section>
    </main>
  );
}

export default Main;
