import React from "react";

function PopupWithForm(props) {
  const [isOpen, setOpen] = React.useState(props.isOpen);

  React.useEffect(() => {
    document.addEventListener('keydown', handleEscClose);
    return () => {
      document.removeEventListener('keydown', handleEscClose);
    }
  }, []);

  function handleEscClose(event) {
    if (event.key === 'Escape') {
      close();
    }
  }

  function close() {
    setOpen(false);
    props.onClose();
  }

  return(
    <>
    <div className={`popup popup_type_form popup_type_${props.name} ${isOpen && 'popup_opened'}`}
      role="dialog" aria-modal="true" tabIndex="-1"
      onMouseDown={close}>
      <button className="popup__close-button button"
        type="button"
        aria-label="Закрыть"
        onMouseDown={close}>
      </button>
      <form className={`popup__container ${props.classContainer} form`} name="edit-profile" noValidate>
        <h2 className="form__title">
          {props.title}
        </h2>
        { props.children }
      </form>
    </div>
  {/*

  <div class="popup popup_type_form popup_type_confirm-delete" role="dialog" aria-modal="true" tabindex="-1">
    <button class="popup__close-button button" type="button" aria-label="Закрыть"></button>
    <div class="popup__container popup__container_type_confirmation form">
      <h2 class="form__title">Вы уверены?</h2>
      <button type="submit" class="form__save-button">Да</button>
    </div>
  </div> */}
    </>
  );
}

export default PopupWithForm;
