import React from "react";

function ImagePopup() {
  const [isOpen, setOpen] = React.useState(props.card);

  function close() {
    setOpen(false);
    props.onClose();
  }

  return (
    <div className={`popup popup_type_zoom-image ${isOpen && 'popup_opened'}`} role="dialog" aria-modal="true" tabIndex="-1">
      <button className="popup__close-button button" type="button" aria-label="Закрыть"
        onMouseDown={close}
      ></button>
      <figure className="popup__container popup__container_type_zoom-image">
        <img src={props.card.link} className="popup__zoom-image" alt=""/>
        <figcaption className="popup__zoom-caption">{props.card.name}</figcaption>
      </figure>
    </div>
  );
}

export default ImagePopup;
