import React from "react";
import Header from "./Header";
import Main from "./Main";
import Footer from "./Footer";
import PopupWithForm from "./PopupWithForm";

function App() {
  const [isEditProfilePopupOpen, setEditProfilePopupOpen] = React.useState(false);
  const [isEditAvatarPopupOpen, setAvatarPopupOpen] = React.useState(false);
  const [isAddPlacePopupOpen, setAddPlacePopupOpen] = React.useState(false);

  function handleEditProfileClick() {
    setEditProfilePopupOpen(true);
    console.log(isEditProfilePopupOpen)
  }

  function handleEditAvatarClick() {
    setAvatarPopupOpen(true);
  }

  function handleAddPlaceClick() {
    console.log('3')
    isAddPlacePopupOpen = true;
  }

  function closeAllPopups() {
    setEditProfilePopupOpen(false);
    setAvatarPopupOpen(false);
  }

  return (
    <div>
      <Header />
      <Main
        onEditProfile={handleEditProfileClick}
        onEditAvatar={handleEditAvatarClick}
        onAddPlace={handleAddPlaceClick}
      />
      <Footer />

      {isEditProfilePopupOpen &&
        <PopupWithForm
          title="Редактировать профиль"
          name="edit-profile"
          classContainer="popup__container_type_two-input"
          isOpen={isEditProfilePopupOpen}
          onClose={closeAllPopups}
        >
          <div className="form__item">
            <input id="name-input" type="text" name="name"
              className="form__input" placeholder="Имя"
              minLength="2" maxLength="40" required/>
            <span className="name-input-error form__input-error"></span>
          </div>
          <div className="form__item">
            <input id="about-input" type="text" name="about"
              className="form__input" placeholder="О себе"
              minLength="2" maxLength="200" required/>
            <span className="about-input-error form__input-error"></span>
          </div>
          <button type="submit" className="form__save-button">Сохранить</button>
        </PopupWithForm>
      }

      {isEditAvatarPopupOpen &&
        <PopupWithForm
          title="Обновить аватар"
          name="edit-avatar"
          classContainer="popup__container_type_one-input"
          isOpen={isEditAvatarPopupOpen}
          onClose={closeAllPopups}
        >
          <div className="form__item">
            <input id="avatar-input" type="url" name="avatar" className="form__input" placeholder="Ссылка на аватар"
              required/>
            <span className="avatar-input-error form__input-error"></span>
          </div>
          <button type="submit" className="form__save-button">Сохранить</button>
        </PopupWithForm>
      }

      {isAddPlacePopupOpen && <PopupWithForm title="Новое место" name="add-image" />}


