import React from "react";
import { CSSTransitionGroup } from 'react-transition-group'

function ImagePopup(props) {
  const [isOpen, setOpen] = React.useState(props.card);

  React.useEffect(() => {
    document.addEventListener('keydown', handleEscClose);
    return () => {
      document.removeEventListener('keydown', handleEscClose);
    }
  }, []);

  function handleEscClose(event) {
    if (event.key === 'Escape') {
      close();
    }
  }

  function close() {
    setOpen(false);
    props.onClose();
  }

  return (
    <CSSTransitionGroup>
    <div className={`popup popup_type_zoom-image ${isOpen && 'popup_opened'}`}
      role="dialog"
      aria-modal="true"
      tabIndex="-1"
      onMouseDown={close}>
      <button className="popup__close-button button"
        type="button"
        aria-label="Закрыть"
        onMouseDown={close}>
      </button>
      <figure className="popup__container popup__container_type_zoom-image">
        <img src={props.card.link}
          className="popup__zoom-image"
          alt={props.card.name} />
        <figcaption className="popup__zoom-caption">
          {props.card.name}
        </figcaption>
      </figure>
    </div>
    </CSSTransitionGroup>
  );
}

export default ImagePopup;
