import React from "react";

function Tooltip(props) {
  const [isOpen, setOpen] = React.useState(props.isOpen);
  const [position, setPosition] = React.useState({ top: 0, left: 0 });
  const tooltip = React.useRef(null);

  React.useEffect(() => {
    setPosition({
      top: props.position.top,
      left: props.position.left
    });

    React.useEffect(() => {
      const coords = tooltip.current.getBoundingClientRect();
      console.log(tooltip.current.getBoundingClientRect())
      console.log(coords.right)
        console.log(document.documentElement.clientWidth)
      if(coords.right > document.documentElement.clientWidth) {
        setPosition({
          left: coords.right - coords.width
        })
        // this._popup.style.right = 0;
  // console.log('1')
      }
    });
  }, []);

  return (
    <div className={`tooltip-likes ${isOpen && 'popup_opened'}`}
      ref={tooltip}
      style={{
            top: position.top,
            left: position.left
          }}>
      {props.likes.map((like, i) => (
        <img key={like._id}
          src={like.avatar}
          className="tooltip-likes__avatar" />
      ))}
    </div>
  );
}

export default Tooltip;
