import Header from "./Header";
import Main from "./Main";
import Footer from "./Footer";
import PopupWithForm from "./PopupWithForm";

function App() {
  let isEditProfilePopupOpen = false;
  let isAddPlacePopupOpen = false;
  let isEditAvatarPopupOpen = false;

  function handleEditAvatarClick() {
    console.log('1')
    isEditAvatarPopupOpen = true;
  }

  function handleEditProfileClick() {
    console.log('2')
    isEditProfilePopupOpen = true;
    // popupEditProfile.open();
    // popupEditProfile.setInputValues(userInfo.getUserInfo());
    // formValidators['editProfile'].resetValidation();
  }

  function handleAddPlaceClick() {
    console.log('3')
    isAddPlacePopupOpen = true;
  }

  return (
    <div className="page">
      <Header />
      <Main
        onEditProfile={handleEditProfileClick}
        onEditAvatar={handleEditAvatarClick}
        onAddPlace={handleAddPlaceClick}
      />
      <Footer />


  <div class="tooltip-likes"></div>
  <template id="gallery-item-template">
    <li class="gallery__list-item">
      <figure class="card">
        <div class="card__aspect-ratio">
          <img class="card__image"/>
        </div>
        <button class="card__delete-button button" type="button" aria-label="Удалить"></button>
        <figcaption class="card__container-caption">
          <h2 class="card__caption"></h2>
          <button class="card__like-button" type="button" aria-label="Лайк"></button>
          <p class="card__like-counter"></p>
        </figcaption>
      </figure>
    </li>
  </template>
  <template id="preview-user-template">
    <img class="tooltip-likes__avatar"/>
  </template>
    </div>
  );
}

export default App;
